import React, { Component } from 'react';
import { Button,View, Text, } from 'react-native';
import moment from 'moment'
export default class DetailScreen extends Component {
    constructor(props){
        super(props);
        const moment = require('moment');

        this.state = {
            weather_0900 : null,
            weather_1200 : null,
            weather_1500 : null,
            weather_1800 : null,
            weather_2100 : null,
            weather_2400 : null,
        }
    }

    componentMount(){
        return fetch("https://api.openweathermap.org/data/2.5/forecast?q="+ this.props.route.params.text+"&appid=e5ea91bc97a4a071bfb4d4d27fe05ae1")
        .then( (response) => response.json() )
        .then( (responseJson) => {
        console.log(responseJson)
            this.setState({
                  weather_0900 : (responseJson.list[0]),
                  weather_1200 : (responseJson.list[0]),
                  weather_1500 : (responseJson.list[0]),
                  weather_1800 : (responseJson.list[0]),
                  weather_2100 : (responseJson.list[0]),
                  weather_2400 : (responseJson.list[0]),
                test : true,
            });
            console.log(this.state);
        })
        .catch((error) => {
            console.log(error)
        });
    }

  render() {
            if(!this.state.test){
                        this.componentMount();
            }
            return (
                      <View>
                        <Text style={{fontSize:30}}>How is the Weather today</Text>
                        <Text>검색한 도시</Text>
                        <Text style={{fontSize:30, backgroundColor: '#aaaaaa', borderRadius: 5, padding:5, margin:5}}>
                        {this.props.route.params.text}
                        </Text>
                      </View>
                    );

    }


}
