import React, { Component } from 'react';
import {SafeAreaView, StatusBar, ScrollView, Button,View, Text, StyleSheet } from 'react-native';
import moment from 'moment'
export default class DetailScreen extends Component {
    constructor(props){
        super(props);
        const moment = require('moment');

        this.state = {
            weather_0900 : [],
            weather_1200 : [],
            weather_1500 : [],
            weather_1800 : [],
            weather_2100 : [],
            weather_2400 : [],
        }
    }

    componentMount(){
        return fetch("https://api.openweathermap.org/data/2.5/forecast?q="+ this.props.route.params.text+"&appid=e5ea91bc97a4a071bfb4d4d27fe05ae1")
        .then( (response) => response.json() )
        .then( (responseJson) => {
        console.log(responseJson)
            this.setState({
                  weather_0900 : (responseJson.list[0]),
                  weather_1200 : (responseJson.list[1]),
                  weather_1500 : (responseJson.list[2]),
                  weather_1800 : (responseJson.list[3]),
                  weather_2100 : (responseJson.list[4]),
                  weather_2400 : (responseJson.list[5]),
                test : true,
            });
            console.log(this.state);
        })
        .catch((error) => {
            console.log(error)
        });
    }

  render() {
            if(!this.state.test){
                        this.componentMount();
            }
            return (
                <SafeAreaView>
                      <View>
                        <Text style={{fontSize:30}}>How is the Weather today</Text>
                        <Text>검색한 도시</Text>
                        <Text style={{fontSize:30, backgroundColor: '#aaaaaa', borderRadius: 5, padding:5, margin:5}}>
                        {this.props.route.params.text}
                        </Text>
                                          <StatusBar style="auto"/>

                      </View>
                    <ScrollView>
                      <View style = {styles.weatherbox}>
                           <Text>
                           {moment(this.state.weather_0900.dt_txt).format("HHmm")}
//                           기온 {this.state.weather_0900.main.temp}
                           </Text>
                                             <StatusBar style="auto"/>

                      </View>
                      <View style = {styles.weatherbox}>
                           <Text>
                           {moment(this.state.weather_1200.dt_txt).format("HHmm")}
//                           기온 {this.state.weather_1200.main.temp}

                           </Text>
                                             <StatusBar style="auto"/>

                      </View>
                      <View style = {styles.weatherbox}>
                           <Text>
                           {moment(this.state.weather_1500.dt_txt).format("HHmm")}
//                           기온 {this.state.weather_1500.main.temp}

                           </Text>
                                             <StatusBar style="auto"/>

                      </View>
                      <View style = {styles.weatherbox}>
                           <Text>
                           {moment(this.state.weather_1800.dt_txt).format("HHmm")}
//                           기온 {this.state.weather_1800.main.temp}

                           </Text>
                                             <StatusBar style="auto"/>

                      </View>
                      <View style = {styles.weatherbox}>
                           <Text>
                           {moment(this.state.weather_2100.dt_txt).format("HHmm")}
//                           기온 {this.state.weather_2100.main.temp}

                           </Text>
                                             <StatusBar style="auto"/>

                      </View>
                      <View style = {styles.weatherbox}>
                           <Text>
                           {moment(this.state.weather_2400.dt_txt).format("HHmm")}
//                           기온 {this.state.weather_2400.main.temp}

                           </Text>
                                             <StatusBar style="auto"/>

                      </View>
                      </ScrollView>
                </SafeAreaView>


            );

    }
}

const styles = StyleSheet.create({
  fullscreen: {
    flex: 1,
  },
  weatherbox: {
    flex:1,
  },
  Search:{
    backgroundColor : 'black',
    alignItems : 'left',
    justifyContent : 'center'

  },
});
